contracts/rewards/RewardsController.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "@openzeppelin/contracts/utils/math/SafeCast.sol";

import "./RewardsDistributor.sol";
import "../interfaces/rewards/IRewardsController.sol";
import "../PostConstruct.sol";
import "../libraries/GPv2SafeERC20.sol";

contract RewardsController is IRewardsController, RewardsDistributor, PostConstruct {
    /// @dev user => claimer
    mapping(address => address) internal _authorizedClaimers;

    /// @dev reward => rewardOracle
    mapping(address => IEACAggregatorProxy) internal _rewardOracle;

    /// @dev Account that secures ERC20 rewards.
    /// @dev It must approve `RewardsController` to spend the rewards it holds.
    address internal REWARDS_VAULT;

    modifier onlyAuthorizedClaimers(address claimer, address user) {
        if (_authorizedClaimers[user] != claimer && address(solidStakingViewActions) != claimer) {
            revert UnauthorizedClaimer(claimer, user);
        }
        _;
    }

    function setup(
        address _solidStakingViewActions,
        address rewardsVault,
        address emissionManager
    ) external postConstruct {
        _setSolidStaking(_solidStakingViewActions);
        _setRewardsVault(rewardsVault);
        _setEmissionManager(emissionManager);
    }

    /// @inheritdoc IRewardsController
    function getRewardsVault() external view returns (address) {
        return REWARDS_VAULT;
    }

    /// @inheritdoc IRewardsController
    function getClaimer(address user) external view returns (address) {
        return _authorizedClaimers[user];
    }

    /// @inheritdoc IRewardsController
    function getRewardOracle(address reward) external view returns (address) {
        return address(_rewardOracle[reward]);
    }

    /// @inheritdoc IRewardsController
    function configureAssets(RewardsDataTypes.DistributionConfig[] memory config)
        external
        onlyEmissionManager
    {
        for (uint i; i < config.length; i++) {
            config[i].totalStaked = solidStakingViewActions.totalStaked(config[i].asset);
            _setRewardOracle(config[i].reward, config[i].rewardOracle);
        }
        _configureAssets(config);
    }

    /// @inheritdoc IRewardsController
    function setRewardOracle(address reward, IEACAggregatorProxy rewardOracle) external onlyEmissionManager {
        _setRewardOracle(reward, rewardOracle);
    }

    /// @inheritdoc IRewardsController
    function setClaimer(address user, address caller) external onlyEmissionManager {
        _authorizedClaimers[user] = caller;
        emit ClaimerSet(user, caller);
    }

    /// @inheritdoc IRewardsController
    function setRewardsVault(address rewardsVault) external onlyEmissionManager {
        _setRewardsVault(rewardsVault);
    }

    function setSolidStaking(address solidStaking) external onlyEmissionManager {
        _setSolidStaking(solidStaking);
    }

    /// @inheritdoc IRewardsController
    function handleUserStakeChanged(
        address asset,
        address user,
        uint oldUserStake,
        uint oldTotalStaked
    ) external {
        if (msg.sender != address(solidStakingViewActions)) {
            revert NotSolidStaking(msg.sender);
        }

        _updateAllRewardDistributionsAndUserRewardsForAsset(asset, user, oldUserStake, oldTotalStaked);
    }

    /// @inheritdoc IRewardsController
    function claimAllRewards(address[] calldata assets, address to)
        external
        returns (address[] memory rewardsList, uint[] memory claimedAmounts)
    {
        if (to == address(0)) {
            revert InvalidInput();
        }

        return _claimAllRewards(assets, msg.sender, msg.sender, to);
    }

    /// @inheritdoc IRewardsController
    function claimAllRewardsOnBehalf(
        address[] calldata assets,
        address user,
        address to
    )
        external
        onlyAuthorizedClaimers(msg.sender, user)
        returns (address[] memory rewardsList, uint[] memory claimedAmounts)
    {
        if (to == address(0) || user == address(0)) {
            revert InvalidInput();
        }

        return _claimAllRewards(assets, msg.sender, user, to);
    }

    /// @inheritdoc IRewardsController
    function claimAllRewardsToSelf(address[] calldata assets)
        external
        returns (address[] memory rewardsList, uint[] memory claimedAmounts)
    {
        return _claimAllRewards(assets, msg.sender, msg.sender, msg.sender);
    }

    /// @inheritdoc RewardsDistributor
    function _getAssetStakedAmounts(address[] calldata assets, address user)
        internal
        view
        override
        returns (RewardsDataTypes.AssetStakedAmounts[] memory assetStakedAmounts)
    {
        assetStakedAmounts = new RewardsDataTypes.AssetStakedAmounts[](assets.length);
        for (uint i; i < assets.length; i++) {
            assetStakedAmounts[i].asset = assets[i];
            assetStakedAmounts[i].userStake = solidStakingViewActions.balanceOf(assets[i], user);
            assetStakedAmounts[i].totalStaked = solidStakingViewActions.totalStaked(assets[i]);
        }
        return assetStakedAmounts;
    }

    /// @dev Claims all accrued rewards for a user on behalf, for the specified asset, accumulating the pending rewards.
    /// @param assets List of assets to check eligible distributions before claiming rewards
    /// @param claimer Address of the claimer on behalf of user
    /// @param user Address to check and claim rewards
    /// @param to Address that will be receiving the rewards
    /// @return
    ///   rewardsList List of reward addresses
    ///   claimedAmount List of claimed amounts, follows "rewardsList" items order
    function _claimAllRewards(
        address[] calldata assets,
        address claimer,
        address user,
        address to
    ) internal returns (address[] memory rewardsList, uint[] memory claimedAmounts) {
        uint rewardsListLength = _rewardsList.length;
        rewardsList = new address[](rewardsListLength);
        claimedAmounts = new uint[](rewardsListLength);

        _updateAllRewardDistributionsAndUserRewardsForAssets(user, _getAssetStakedAmounts(assets, user));

        for (uint i; i < assets.length; i++) {
            address asset = assets[i];
            for (uint j; j < rewardsListLength; j++) {
                if (rewardsList[j] == address(0)) {
                    rewardsList[j] = _rewardsList[j];
                }
                uint rewardAmount = _assetData[asset]
                    .rewardDistribution[rewardsList[j]]
                    .userReward[user]
                    .accrued;
                if (rewardAmount != 0) {
                    claimedAmounts[j] += rewardAmount;
                    _assetData[asset].rewardDistribution[rewardsList[j]].userReward[user].accrued = 0;
                }
            }
        }
        for (uint i; i < rewardsListLength; i++) {
            _transferRewards(to, rewardsList[i], claimedAmounts[i]);
            emit RewardsClaimed(user, rewardsList[i], to, claimer, claimedAmounts[i]);
        }
        return (rewardsList, claimedAmounts);
    }

    /// @dev Function to transfer rewards to the desired account
    /// @param to Account address to send the rewards
    /// @param reward Address of the reward token
    /// @param amount Amount of rewards to transfer
    function _transferRewards(
        address to,
        address reward,
        uint amount
    ) internal {
        GPv2SafeERC20.safeTransferFrom(IERC20(reward), REWARDS_VAULT, to, amount);
    }

    /// @dev Update the Price Oracle of a reward token. The Price Oracle must follow Chainlink IEACAggregatorProxy interface.
    /// @notice The Price Oracle of a reward is used for displaying correct data about the incentives at the UI frontend.
    /// @param reward The address of the reward token
    /// @param rewardOracle The address of the price oracle
    function _setRewardOracle(address reward, IEACAggregatorProxy rewardOracle) internal {
        if (rewardOracle.latestAnswer() <= 0) {
            revert InvalidRewardOracle(reward, address(rewardOracle));
        }

        _rewardOracle[reward] = rewardOracle;
        emit RewardOracleUpdated(reward, address(rewardOracle));
    }

    function _setRewardsVault(address rewardsVault) internal {
        REWARDS_VAULT = rewardsVault;
        emit RewardsVaultUpdated(rewardsVault);
    }

    function _setSolidStaking(address solidStaking) internal {
        solidStakingViewActions = ISolidStakingViewActions(solidStaking);
        emit SolidStakingUpdated(solidStaking);
    }
}

contracts/rewards/EmissionManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "../interfaces/rewards/IEmissionManager.sol";
import "../interfaces/manager/IWeeklyCarbonRewardsManager.sol";
import "../PostConstruct.sol";

/// @title EmissionManager
/// @author Aave
/// @notice It manages the list of admins of reward emissions and provides functions to control reward emissions.
contract EmissionManager is Ownable, IEmissionManager, PostConstruct, ReentrancyGuard {
    // reward => emissionAdmin
    mapping(address => address) internal _emissionAdmins;

    IWeeklyCarbonRewardsManager internal _carbonRewardsManager;
    IRewardsController internal _rewardsController;

    modifier onlyEmissionAdmin(address reward) {
        if (_emissionAdmins[reward] != msg.sender) {
            revert NotEmissionAdmin(msg.sender, reward);
        }
        _;
    }

    function setup(
        address carbonRewardsManager,
        address controller,
        address owner
    ) external postConstruct {
        _setCarbonRewardsManager(carbonRewardsManager);
        _setRewardsController(controller);
        transferOwnership(owner);
    }

    /// @inheritdoc IEmissionManager
    function configureAssets(RewardsDataTypes.DistributionConfig[] memory config) external {
        for (uint i; i < config.length; i++) {
            if (_emissionAdmins[config[i].reward] != msg.sender) {
                revert NotEmissionAdmin(msg.sender, config[i].reward);
            }
        }
        _rewardsController.configureAssets(config);
    }

    /// @inheritdoc IEmissionManager
    function setRewardOracle(address reward, IEACAggregatorProxy rewardOracle)
        external
        onlyEmissionAdmin(reward)
    {
        _rewardsController.setRewardOracle(reward, rewardOracle);
    }

    /// @inheritdoc IEmissionManager
    function setDistributionEnd(
        address asset,
        address reward,
        uint32 newDistributionEnd
    ) external onlyEmissionAdmin(reward) {
        _rewardsController.setDistributionEnd(asset, reward, newDistributionEnd);
    }

    /// @inheritdoc IEmissionManager
    function setEmissionPerSecond(
        address asset,
        address[] calldata rewards,
        uint88[] calldata newEmissionsPerSecond
    ) external {
        for (uint i; i < rewards.length; i++) {
            if (_emissionAdmins[rewards[i]] != msg.sender) {
                revert NotEmissionAdmin(msg.sender, rewards[i]);
            }
        }
        _rewardsController.setEmissionPerSecond(asset, rewards, newEmissionsPerSecond);
    }

    /// @inheritdoc IEmissionManager
    function updateCarbonRewardDistribution(address[] calldata assets, uint[] calldata categoryIds)
        external
        nonReentrant
    {
        if (assets.length != categoryIds.length) {
            revert InvalidInput();
        }

        (
            address[] memory carbonRewards,
            uint[] memory rewardAmounts,
            uint[] memory rewardFees
        ) = _carbonRewardsManager.computeWeeklyCarbonRewards(categoryIds);

        _rewardsController.updateCarbonRewardDistribution(assets, carbonRewards, rewardAmounts);

        _carbonRewardsManager.mintWeeklyCarbonRewards(
            categoryIds,
            carbonRewards,
            rewardAmounts,
            rewardFees,
            _rewardsController.getRewardsVault()
        );
    }

    /// @inheritdoc IEmissionManager
    function setClaimer(address user, address claimer) external onlyOwner {
        _rewardsController.setClaimer(user, claimer);
    }

    /// @inheritdoc IEmissionManager
    function setRewardsVault(address rewardsVault) external onlyOwner {
        _rewardsController.setRewardsVault(rewardsVault);
    }

    /// @inheritdoc IEmissionManager
    function setEmissionManager(address emissionManager) external onlyOwner {
        _rewardsController.setEmissionManager(emissionManager);
    }

    /// @inheritdoc IEmissionManager
    function setSolidStaking(address solidStaking) external onlyOwner {
        _rewardsController.setSolidStaking(solidStaking);
    }

    /// @inheritdoc IEmissionManager
    function setEmissionAdmin(address reward, address admin) external onlyOwner {
        address oldAdmin = _emissionAdmins[reward];
        _emissionAdmins[reward] = admin;
        emit EmissionAdminUpdated(reward, oldAdmin, admin);
    }

    /// @inheritdoc IEmissionManager
    function setRewardsController(address controller) external onlyOwner {
        _setRewardsController(controller);
    }

    /// @inheritdoc IEmissionManager
    function setCarbonRewardsManager(address carbonRewardsManager) external onlyOwner {
        _setCarbonRewardsManager(carbonRewardsManager);
    }

    /// @inheritdoc IEmissionManager
    function getRewardsController() external view returns (IRewardsController) {
        return _rewardsController;
    }

    /// @inheritdoc IEmissionManager
    function getEmissionAdmin(address reward) external view returns (address) {
        return _emissionAdmins[reward];
    }

    /// @inheritdoc IEmissionManager
    function getCarbonRewardsManager() external view returns (address) {
        return address(_carbonRewardsManager);
    }

    function _setCarbonRewardsManager(address carbonRewardsManager) internal {
        _carbonRewardsManager = IWeeklyCarbonRewardsManager(carbonRewardsManager);

        emit CarbonRewardsManagerUpdated(carbonRewardsManager);
    }

    function _setRewardsController(address controller) internal {
        _rewardsController = IRewardsController(controller);

        emit RewardsControllerUpdated(controller);
    }
}

contracts/interfaces/rewards/IEmissionMa.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import "../../libraries/RewardsDataTypes.sol";
import "./IRewardsController.sol";

/// @title IEmissionManager
/// @author Aave
/// @notice Defines the basic interface for the Emission Manager
interface IEmissionManager {
    error NotEmissionAdmin(address sender, address reward);

    event EmissionAdminUpdated(address indexed reward, address indexed oldAdmin, address indexed newAdmin);
    event RewardsControllerUpdated(address indexed newRewardsController);
    event CarbonRewardsManagerUpdated(address indexed newCarbonRewardsManager);

    error InvalidInput();

    /// @dev Configure assets to incentivize with an emission of rewards per second until the end of distribution.
    /// @dev Only callable by the emission admin of the given rewards
    /// @param config The assets configuration input, the list of structs contains the following fields:
    ///   uint104 emissionPerSecond: The emission per second following rewards unit decimals.
    ///   uint256 totalSupply: The total supply of the asset to incentivize
    ///   uint40 distributionEnd: The end of the distribution of the incentives for an asset
    ///   address asset: The asset address to incentivize
    ///   address reward: The reward token address
    ///   IEACAggregatorProxy rewardOracle: The Price Oracle of a reward to visualize the incentives at the UI Frontend.
    ///                                     Must follow Chainlink Aggregator IEACAggregatorProxy interface to be compatible.
    function configureAssets(RewardsDataTypes.DistributionConfig[] memory config) external;

    /// @dev Sets an Aave Oracle contract to enforce rewards with a source of value.
    /// @dev Only callable by the emission admin of the given reward
    /// @notice At the moment of reward configuration, the Incentives Controller performs
    /// a check to see if the reward asset oracle is compatible with IEACAggregator proxy.
    /// This check is enforced for integrators to be able to show incentives at
    /// the current Aave UI without the need to setup an external price registry
    /// @param reward The address of the reward to set the price aggregator
    /// @param rewardOracle The address of price aggregator that follows IEACAggregatorProxy interface
    function setRewardOracle(address reward, IEACAggregatorProxy rewardOracle) external;

    /// @dev Sets the end date for the distribution
    /// @dev Only callable by the emission admin of the given reward
    /// @param asset The asset to incentivize
    /// @param reward The reward token that incentives the asset
    /// @param newDistributionEnd The end date of the incentivization, in unix time format
    function setDistributionEnd(
        address asset,
        address reward,
        uint32 newDistributionEnd
    ) external;

    /// @dev Sets the emission per second of a set of reward distributions
    /// @param asset The asset is being incentivized
    /// @param rewards List of reward addresses are being distributed
    /// @param newEmissionsPerSecond List of new reward emissions per second
    function setEmissionPerSecond(
        address asset,
        address[] calldata rewards,
        uint88[] calldata newEmissionsPerSecond
    ) external;

    /// @dev Computes and mints weekly carbon rewards, and instructs RewardsController how to distribute them
    /// @param assets The incentivized assets (hypervisors)
    /// @param _categoryIds The categories to which the incentivized assets belong
    function updateCarbonRewardDistribution(address[] calldata assets, uint[] calldata _categoryIds) external;

    /// @dev Whitelists an address to claim the rewards on behalf of another address
    /// @dev Only callable by the owner of the EmissionManager
    /// @param user The address of the user
    /// @param claimer The address of the claimer
    function setClaimer(address user, address claimer) external;

    /// @dev Only callable by the owner of the EmissionManager
    /// @param rewardsVault The address of the account that secures ERC20 rewards.
    function setRewardsVault(address rewardsVault) external;

    /// @dev Only callable by the owner of the EmissionManager
    /// @param solidStaking Used to fetch the total amount staked and the stake of an user for a given asset
    function setSolidStaking(address solidStaking) external;

    /// @dev Updates the address of the emission manager
    /// @dev Only callable by the owner of the EmissionManager
    /// @param emissionManager The address of the new EmissionManager
    function setEmissionManager(address emissionManager) external;

    /// @dev Updates the admin of the reward emission
    /// @dev Only callable by the owner of the EmissionManager
    /// @param reward The address of the reward token
    /// @param admin The address of the new admin of the emission
    function setEmissionAdmin(address reward, address admin) external;

    /// @dev Updates the address of the rewards controller
    /// @dev Only callable by the owner of the EmissionManager
    /// @param controller the address of the RewardsController contract
    function setRewardsController(address controller) external;

    /// @dev Only callable by the owner of the EmissionManager
    /// @param carbonRewardsManager the address of the IWeeklyCarbonRewardsManager contract
    function setCarbonRewardsManager(address carbonRewardsManager) external;

    /// @dev Returns the rewards controller address
    /// @return The address of the RewardsController contract
    function getRewardsController() external view returns (IRewardsController);

    /// @dev Returns the admin of the given reward emission
    /// @param reward The address of the reward token
    /// @return The address of the emission admin
    function getEmissionAdmin(address reward) external view returns (address);

    /// @return The address of the IWeeklyCarbonRewardsManager implementation contract
    function getCarbonRewardsManager() external view returns (address);
}

